package com.univ.algorithom.sort;import org.junit.Test;import java.util.Arrays;/** * Created by Univ on 16/5/15. */public class BubbleSort {    @Test    public void test() {        int[] a = {3, 9, 20, 12, 4, 7, 20, 5,223,100,17};        int[] b = bubbleSort(a, 0, a.length - 1);        System.out.println(Arrays.toString(b));    }    private int[] bubbleSort(int[] a, int lo, int hi) {        for (int i = lo; i <= hi - 1; i++) {            for (int j = 0; j +  1 <= hi - i; j++) {                if (a[j ] > a[j + 1]) {                    swap(a, j, j+1);                }            }        }        return a;    }    private void swap(int[] a, int i, int j) {        int temp = a[i];        a[i] = a[j];        a[j] = temp;    }}